mapper.select:
select wp.id, wp.product_id, wp.warehouse_id, wp.apply_num, wp.apply_price, wp.apply_time, wp.real_num, wp.apply_state,(case wp.apply_state when 1 then '待审核' when 2 then '通过' when 3 then '拒绝' when 4 then '完成' end) applyStateName, wp.remark, wp.sys_user_id, p.product_name, w.warehouse_name from t_warehouse_purchase wp
left join t_product p on wp.product_id=p.id left join t_warehouse w on wp.warehouse_id=w.id
left join sys_user u on u.user_id= wp.sys_user_id
left join sys_dept d on u.dept_id = d.dept_id
where u.del_flag = '0'
 and product_id = #{productId}
 and warehouse_id = #{warehouseId}
 and apply_num = #{applyNum}
 and apply_price = #{applyPrice}
 and apply_time = #{applyTime}
 and real_num = #{realNum}
 and apply_state = #{applyState}
 and wp.sys_user_id = #{sysUserId}
${params.dataScope}

select wp.id, wp.product_id, wp.warehouse_id, wp.apply_num, wp.apply_price, wp.apply_time, wp.real_num, wp.apply_state,(case wp.apply_state when 1 then '待审核' when 2 then '通过' when 3 then '拒绝' when 4 then '完成' end) applyStateName, wp.remark, wp.sys_user_id, p.product_name, w.warehouse_name from t_warehouse_purchase wp
left join t_product p on wp.product_id=p.id left join t_warehouse w on wp.warehouse_id=w.id
left join sys_user u on u.user_id= w.sys_user_id
left join sys_dept d on u.dept_id = d.dept_id
where u.del_flag = '0'
 and product_id = #{productId}
 and warehouse_id = #{warehouseId}
 and apply_num = #{applyNum}
 and apply_price = #{applyPrice}
 and apply_time = #{applyTime}
 and real_num = #{realNum}
 and apply_state = #{applyState}
 and sys_user_id = #{sysUserId}
${params.dataScope}

mapper.update:
update t_warehouse_purchase
product_id = #{productId}, warehouse_id = #{warehouseId}, apply_num = #{applyNum}, apply_price = #{applyPrice}, apply_time = #{applyTime}, real_num = #{realNum}, apply_state = #{applyState}, remark = #{remark}, sys_user_id = #{sysUserId},
where id = #{id}
