mapper.select:

LEFT JOIN t_warehouse_purchaseDetail pd ON wp.id = pd.purchase_id
LEFT JOIN t_product p ON pd.product_id = p.id
LEFT JOIN t_warehouse w ON wp.warehouse_id = w.id
LEFT JOIN t_supplier ts ON wp.supplier_id = ts.id
LEFT JOIN sys_user u ON u.user_id = wp.sys_user_id
LEFT JOIN sys_dept d ON u.dept_id = d.dept_id
where u.del_flag = '0' and wp.del_flag=1
 and warehouse_id = #{warehouseId}
 and supplier_id = #{supplierId}
 and apply_state = #{applyState}
 and wp.sys_user_id = #{sysUserId}
${params.dataScope}


LEFT JOIN t_warehouse_purchaseDetail pd ON wp.id = pd.purchase_id
LEFT JOIN t_product p ON pd.product_id = p.id
left join t_warehouse w on wp.warehouse_id=w.id
left join t_supplier ts on wp.supplier_id=ts.id
left join sys_user u on u.user_id= w.sys_user_id
left join sys_dept d on u.dept_id = d.dept_id
where u.del_flag = '0' and wp.del_flag=1
 and warehouse_id = #{warehouseId}
 and supplier_id = #{supplierId}
 and apply_state = #{applyState}
 and sys_user_id = #{sysUserId}
${params.dataScope}

mapper.update:
update t_warehouse_purchase
warehouse_id = #{warehouseId}, supplier_id = #{supplierId}, apply_state = #{applyState}, sys_user_id = #{sysUserId}, create_by = #{createBy}, now_check_id = #{nowCheckId}, check_step = #{checkStep}, check_ids = #{checkIds}, check_index = #{checkIndex}, create_time = #{createTime}, update_time = #{updateTime}, version = #{version}, remark = #{remark}, del_flag = #{delFlag}, deposit = #{deposit},
where id = #{id}
