mapper.select:
select s.id, s.product_id, s.product_code, s.warehouse_id, s.price, s.makert_code, (case s.surplus_type when 1 then '入库' when 2 then '出库' end) surplusTypeName, (case s.detail_type when 1 then '生产入库' when 2 then '调拨入库' when 3 then '售后入库' when 4 then '其他入库' when 5 then '采购入库' when 6 then '销售出库' when 7 then '调拨出库' when 8 then '其他出库' end) detailTypeName, s.bak,p.product_name, w.warehouse_name from t_warehouse_surplusdetails s
left join t_product p on s.product_id=p.id left join t_warehouse w on s.warehouse_id=w.id
left join sys_user u on u.user_id= w.sys_user_id
left join sys_dept d on u.dept_id = d.dept_id
where u.del_flag = '0'
 and product_id = #{productId}
 and product_code = #{productCode}
 and warehouse_id = #{warehouseId}
 and price = #{price}
 and makert_code = #{makertCode}
 and surplus_type = #{surplusType}
 and detail_type = #{detailType}
 and bak = #{bak}
${params.dataScope}

mapper.update:
update t_warehouse_surplusdetails
product_id = #{productId}, product_code = #{productCode}, warehouse_id = #{warehouseId}, price = #{price}, makert_code = #{makertCode}, create_time = #{createTime}, surplus_type = #{surplusType}, detail_type = #{detailType}, bak = #{bak},
where id = #{id}
